class Query {
  constructor (query = '', db) {
    /**
     * DO NOT ACCESS query OR db DIRECTLY
     * @private
     */
    this.query = query
    this.db = db
  }

  /**
   * @name run
   * @returns A promise, running the current query.
   */
  run () {
    return this.db.query(this.query)
  }
}

module.exports = Query
